    private void botao_estadoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botao_estadoActionPerformed
        // TODO add your handling code here:
         texto_dinheiro.setText("0,00");
         texto_Estado.setText("ON");
         
         if (Controle.isLigada() == true){
             texto_Estado.setText("OFF");
             Controle.desligar();
             saldo = 0;
             
         }else{
         Controle.ligar();
         }
    }//GEN-LAST:event_botao_estadoActionPerformed

    private void botao_cafeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botao_cafeActionPerformed
        // TODO add your handling code here:
        if(Controle.isLigada() == true){
        saldo += 1.00;
        resultado = String.valueOf(saldo);
        texto_dinheiro.setText(resultado);
        }
    }//GEN-LAST:event_botao_cafeActionPerformed

    private void botao_cafecleiteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botao_cafecleiteActionPerformed
        // TODO add your handling code here:
        if(Controle.isLigada()==true){
        saldo += 1.50;
        resultado = String.valueOf(saldo);
        texto_dinheiro.setText(resultado);
        }
    }//GEN-LAST:event_botao_cafecleiteActionPerformed

    private void botao_capuccinoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botao_capuccinoActionPerformed
        // TODO add your handling code here:
        if(Controle.isLigada()== true){
        saldo += 2.00;
        resultado = String.valueOf(saldo);
        texto_dinheiro.setText(resultado);
        }
    }//GEN-LAST:event_botao_capuccinoActionPerformed

